mapper.insert:

SET @new_task_id = (
    SELECT IFNULL(MAX(task_id), 0) + 1
    FROM DOIT.tb_todolist
    WHERE user_id = #{user_id}
);


SET @new_task_order = (
    SELECT IFNULL(MAX(task_order), 0) + 1
    FROM DOIT.tb_todolist
    WHERE user_id = #{user_id} AND task_date = #{task_date}
);


INSERT INTO DOIT.tb_todolist (
    user_id,
    task_id,
    task_title,
    task_date,
    task_order,
    is_completed,
    created_dt
) VALUES (
    #{user_id},
    @new_task_id,
    #{task_title},
    #{task_date},
    @new_task_order,
    0,
    CURRENT_TIMESTAMP()
);

mapper.update:
	UPDATE tb_todolist
	SET is_completed = 1,
        updated_dt = CURRENT_TIMESTAMP()
    WHERE
    	user_id = #{user_id}
    	AND task_date = #{task_date}
    	AND task_id = #{task_id}
